local Keys = {["F6"] = 167, ["E"] = 38, ["DELETE"] = 178}
local debug = Config.debug

ESX                     = nil
local isLoading         = true

local zoneList          = {} -- { {enable, gps, markerD, blipD, blip, name}, ...}
local alreadyInZone     = false
local lastZone          = nil

local currentAction     = nil
local currentActionMsg  = ''

local isWorking         = false
local isRunning         = false
local currentRun        = {}

-- teleport
function TeleportFadeEffect(coords)
  Citizen.CreateThread(function()
    DoScreenFadeOut(800)
    while not IsScreenFadedOut() do Citizen.Wait(1000) end
    SetEntityCoords(GetPlayerPed(-1), coords.x, coords.y, coords.z)
    DoScreenFadeIn(800)
  end)
end

-- debug msg
function printDebug(msg)
 -- if debug then print('[esx_journalist] ' .. msg) end
end

-- init
Citizen.CreateThread(function()
  local startLoad = GetGameTimer()
  while ESX == nil do
    Citizen.Wait(1000)
    TriggerEvent('esx:getSharedObject', function(obj) ESX = obj end)
  end
  -- load player
  local playerData = ESX.GetPlayerData()
  while playerData == nil do
    Citizen.Wait(1000)
    playerData = ESX.GetPlayerData()
  end
  while playerData.job == nil do
    Citizen.Wait(1000)
    playerData = ESX.GetPlayerData()
  end
  while playerData.job.name == nil do
    Citizen.Wait(1000)
    playerData = ESX.GetPlayerData()
  end
  -- load zone
  for k,v in pairs(Config.zones) do
    local zone = v
    zone.name = k
    -- draw cloakRoom blip for everyone
    if k == 'cloakRoom' then zone.blip = drawBlip(zone.gps, zone.blipD)
    else zone.blip = nil end

    if playerData.job.name == 'journalist' then
      if k == 'cloakRoom' then 
        zone.enable = true
      elseif k == 'roof' and playerData.job.grade >= Config.journalistMinGrade then 
        zone.enable = true
      end
    end
    table.insert(zoneList, zone)
  end
  -- init end
  isLoading = false 
 -- printDebug('Loaded in ' .. tostring(GetGameTimer() - startLoad) .. 'ms')
end)
