-- job/grade change detection
AddEventHandler('esx_journalist:gradeChange', function(grade)
--  printDebug('gradeChange: ' .. grade)
  -- disable all zone except cloakroom
  for i=1, #zoneList, 1 do
    if zoneList[i].name ~= 'cloakRoom' then
      if zoneList[i].name == 'roof' then
        if grade < Config.journalistMinGrade then 
          zoneList[i].enable = false
        else
          zoneList[i].enable = true
        end
      else
        zoneList[i].enable = false
      end
    end
  end
  --clear var
  isWorking = false
  isRunning = false
  currentRun = {}
end)

AddEventHandler('esx_journalist:jobChange', function(currentJob)
  --printDebug('jobChange: ' .. currentJob)
  if currentJob == 'journalist' then
    -- enable cloakroom
    for i=1, #zoneList, 1 do
      if zoneList[i].name == 'cloakRoom' then 
        zoneList[i].enable = true
        break
      end
    end
  else
    -- disable all zone
    for i=1, #zoneList, 1 do 
      zoneList[i].enable = false
    end
    -- clear var
    isWorking = false
    isRunning = false
    currentRun = {}
  end
end)


Citizen.CreateThread(function()
  while isLoading do Citizen.Wait(5000) end
  local playerData = ESX.GetPlayerData()
  local lastJob = playerData.job.name
  local lastGrade = playerData.job.grade
  while true do
    Citizen.Wait(10000)
    playerData = ESX.GetPlayerData()
    if playerData.job.name == 'journalist' then
      -- update on grade change
      if lastGrade ~= playerData.job.grade then
        TriggerEvent('esx_journalist:gradeChange', playerData.job.grade)
        lastGrade = playerData.job.grade
      end
      -- update on job change
      if lastJob ~= 'journalist' then
        TriggerEvent('esx_journalist:jobChange', playerData.job.name)
        lastJob = playerData.job.name
      end
    else
      -- update on job change
      if lastJob == 'journalist' then
        TriggerEvent('esx_journalist:jobChange', playerData.job.name)
        lastJob = playerData.job.name
      end
    end  
  end
end)

-- blip
function drawBlip(gps, blipData)
  printDebug('drawBlip')
  local blip = AddBlipForCoord(gps.x, gps.y, gps.z)
  if not (blipData.sprite == nil)  then SetBlipSprite (blip, blipData.sprite)     end
  if not (blipData.display == nil) then SetBlipDisplay(blip, blipData.display)    end
  if not (blipData.scale == nil)   then SetBlipScale  (blip, blipData.scale)      end
  if not (blipData.color == nil)   then SetBlipColour (blip, blipData.color)      end
  if not (blipData.range == nil)   then SetBlipAsShortRange(blip, blipData.range) end
  if not (blipData.route == nil)   then SetBlipRoute(blip, blipData.route)        end
  if not (blipData.name == nil)    then
    BeginTextCommandSetBlipName("STRING")
    AddTextComponentString(blipData.name)
    EndTextCommandSetBlipName(blip)
  end
  return blip
end


