-- start/stop run
function randomizeList(list)
  local newlist = {}
  while #list > 0 do
    local index = GetRandomIntInRange(1, #list)
    table.insert(newlist, list[index])
    local tmpList = {}
    for i=1, #list, 1 do
      if i ~= index then table.insert(tmpList, list[i]) end
    end
    list = tmpList
  end
  return newlist
end

function genRunList()
  local playerData = ESX.GetPlayerData()
  local coordsList = {}
  if playerData.job.grade >= Config.journalistMinGrade then
    -- liste random des quartiers
    local listQuartier = {}
    for i=1, #Config.journalistBoxes,1 do table.insert(listQuartier, i) end
    listQuartier = randomizeList(listQuartier)
    printDebug(json.encode(listQuartier))
    for i=1, #listQuartier, 1 do 
      local tmpList = randomizeList(Config.journalistBoxes[listQuartier[i]])
      for y=1, #tmpList,1 do table.insert(coordsList, tmpList[y]) end
    end
  else
    local listQuartier = {}
    for i=1, #Config.interimBoxes,1 do table.insert(listQuartier, i) end
    listQuartier = randomizeList(listQuartier)
    printDebug(json.encode(listQuartier))
    for i=1, #listQuartier, 1 do 
      local tmpList = randomizeList(Config.interimBoxes[listQuartier[i]])
      for y=1, #tmpList,1 do table.insert(coordsList, tmpList[y]) end
    end
  end 
  currentRun = coordsList
  printDebug('genRunList: ' .. #currentRun)
end

RegisterNetEvent('esx_journalist:nextBoxes')
AddEventHandler('esx_journalist:nextBoxes', function()
  local tmpList = {}
  for i=1, #currentRun, 1 do
    if i ~= 1 then table.insert(tmpList, currentRun[i]) end
  end
  currentRun = tmpList
  if #currentRun == 0 then genRunList() end
  local playerData = ESX.GetPlayerData()
  for i=1, #zoneList, 1 do
    if playerData.job.grade < Config.journalistMinGrade and zoneList[i].name == 'interimBoxes' then 
      if DoesBlipExist(zoneList[i].blip) then RemoveBlip(zoneList[i].blip) end
      zoneList[i].gps = currentRun[1]
      zoneList[i].enable = true
      zoneList[i].blip = nil
      break
    elseif playerData.job.grade >= Config.journalistMinGrade and zoneList[i].name == 'journalistBoxes' then 
      if DoesBlipExist(zoneList[i].blip) then RemoveBlip(zoneList[i].blip) end
      zoneList[i].gps = currentRun[1]
      zoneList[i].enable = true
      zoneList[i].blip = nil
      break
    end
  end
  ESX.ShowNotification(_U('gps_info'))
  printDebug('nextBoxes: ' .. #currentRun)
end)

function startNativeRun()
  printDebug('startNativeRun: ' .. #currentRun)
  local playerData = ESX.GetPlayerData()
  for i=1, #zoneList, 1 do
    if playerData.job.grade < Config.journalistMinGrade and zoneList[i].name == 'interimBoxes' then 
      zoneList[i].gps = currentRun[1]
      zoneList[i].enable = true
    elseif playerData.job.grade >= Config.journalistMinGrade and zoneList[i].name == 'journalistBoxes' then
      zoneList[i].gps = currentRun[1]
      zoneList[i].enable = true
    end
  end
  ESX.ShowNotification(_U('gps_info'))
end

function stopNativeJob()
  printDebug('stopNativeJob: ' .. #currentRun)
  local playerData = ESX.GetPlayerData()
  for i=1, #zoneList, 1 do
    if playerData.job.grade < Config.journalistMinGrade and zoneList[i].name == 'interimBoxes' then 
      zoneList[i].enable = false
      break
    elseif playerData.job.grade >= Config.journalistMinGrade and zoneList[i].name == 'journalistBoxes' then
      zoneList[i].enable = false
      break
    end
  end
  ESX.ShowNotification(_U('cancel_mission'))
end

Citizen.CreateThread(function()
  while isLoading do Citizen.Wait(10) end
  while true do
    Citizen.Wait(2500)
    local playerData = ESX.GetPlayerData()
    if playerData.job.name == 'journalist' then
      if IsControlJustReleased(1, Keys["DELETE"]) and isWorking then
        if #currentRun == 0 then genRunList() end      
        if isRunning then
          stopNativeJob()
          isRunning = false
        else
          startNativeRun()
          isRunning = true
        end
      end
    end
  end
end)
